where.if:
AND (



            a.major_code IN

                #{code}




                OR

            (

                a.major_code LIKE CONCAT(#{code}, '%')

            )




            a.major_code LIKE CONCAT(#{code}, '%')



)

mapper.select:
SELECT DISTINCT a.year
FROM biz_adjustment a
WHERE a.school_id = #{schoolId}
  AND a.major_id = #{majorId}
  AND a.college_id = #{collegeId}
  AND a.study_mode = #{studyMode}
ORDER BY a.year DESC

SELECT a.school_id AS schoolId, MAX(s.school_logo) AS schoolLogo, a.college_id AS collegeId, a.major_id AS majorId, MAX(a.major_code) AS majorCode, MAX(a.major_name) AS majorName, MAX(a.college_name) AS collegeName, a.year AS year, a.study_mode AS studyMode, MAX(IFNULL(a.adjust_count, 0)) AS adjustCount FROM biz_adjustment a LEFT JOIN biz_school s ON s.id = a.school_id WHERE a.school_id = #{reqVO.schoolId}
AND a.year >= #{reqVO.beginYear}
AND a.year <= #{reqVO.endYear}
GROUP BY a.school_id, a.college_id, a.major_id, a.year, a.study_mode ORDER BY a.year DESC, MAX(a.create_time) DESC
