mapper.select:
SELECT AVG(first_score) FROM biz_adjustment_admit
WHERE deleted = 0
AND school_id = #{schoolId}
 AND college_id = #{collegeId} 
 AND major_code = #{majorCode} 
 AND year = #{year}

SELECT school_id, college_id, major_code, year, MIN(first_score) as min_score, MAX(first_score) as max_score, AVG(first_score) as avg_score, COUNT(*) as count FROM biz_adjustment_admit WHERE deleted = 0 AND school_id IN
#{id}
AND year IN
#{y}
GROUP BY school_id, college_id, major_code, year

SELECT
    MIN(first_score) as minScore,
    MAX(first_score) as maxScore,
    AVG(first_score) as avgScore,
    COUNT(*) as count
FROM biz_adjustment_admit
WHERE deleted = 0
AND school_id = #{schoolId}
 AND college_id = #{collegeId} 
 AND major_code = #{majorCode} 
 AND year = #{year}

SELECT first_score
FROM biz_adjustment_admit
WHERE deleted = 0
AND school_id = #{schoolId}
 AND college_id = #{collegeId} 
 AND major_code = #{majorCode} 
 AND year = #{year} 
ORDER BY first_score ASC

SELECT candidate_name, first_score, first_school_name
FROM biz_adjustment_admit
WHERE deleted = 0
AND school_id = #{schoolId}
 AND college_id = #{collegeId} 
 AND major_code = #{majorCode} 
 AND year = #{year} 
ORDER BY first_score DESC
LIMIT 20
